import React from "react";
import { useScrollTo } from "react-use-window-scroll";

type TProps = {}


const ScrollToTopButton: React.FC<TProps> = React.memo(() => {

    const scrollTo = useScrollTo()


    const scrollToTop = () => {
        console.log('click')

        scrollTo({
            top: 0,
            left: 0,
            behavior: 'smooth'
        })
    } 


    return (
        <div onClick={scrollToTop} className='w-16 h-16 bg-slate-400 opacity-50 cursor-pointer'>

        </div>
    )
})

export default ScrollToTopButton
