import React, {useState} from "react";
import classNames from "classnames";
import Arrow from "../../assets/Arrow/Arrow";
import { TNavItem } from "../Navbar/NavbarContainer";

type TProps = {
    makeMainNavbar: (navItems: Array<TNavItem>) => void
    catalogItems: Array<TNavItem>
}

const Catalog: React.FC<TProps> = React.memo(({makeMainNavbar, catalogItems}) => {


    const [isCatalogShowing, setIsCatalogShowing] = useState(true)


    const arrowClassNames = classNames({
        'absolute': true,
        'top-1': isCatalogShowing,
        '-top-0.3': !isCatalogShowing
    })

    const showCatalog = () => {
        setIsCatalogShowing(!isCatalogShowing)
    }

    return (
        <React.Fragment>
            <div onClick={showCatalog} className="relative">
                <span className="mr-3">каталог продукции</span>
                <span className={arrowClassNames}><Arrow isUp={isCatalogShowing} /></span>
            </div>

            <ul className="bg-[#222] text-white -ml-1.5 pl-1.5 space-y-2"> 
                    {makeMainNavbar(catalogItems)}
            </ul>
        </React.Fragment>

    )
})

export default Catalog
