import React, { EventHandler, useEffect, useRef } from "react";
import { FaUser, FaShoppingCart } from "react-icons/fa";
import { useOnClickOutside } from "usehooks-ts";
import makeNavbar from '../../assets/navbarCreater'
import showControl from "../../assets/showControl/showControl";
import { TNavItem } from '../../types/types'

type TProps = {
    menuItems: Array<TNavItem>
    isMenuOpen: boolean 
    setIsMenuOpen: (isMenuOpen: boolean ) => void
    stylesX: string
    Icon: typeof FaUser | typeof FaShoppingCart
}

const Menu: React.FC<TProps> = React.memo(({menuItems, setIsMenuOpen, isMenuOpen, stylesX, Icon}) => {

    const icon = useRef(null)
    const menu = useRef(null)

    const onToggleLoginMenu = (): void => {
        if (isMenuOpen) {
            closeMenu()
        } else {
            setIsMenuOpen(true)
        }
    }

    const closeMenu = () => {
        setIsMenuOpen(false)
    }

    useEffect(() => {
        useOnClickOutside(menu, onToggleLoginMenu)
    }, [isMenuOpen])


    //useOnClickOutside(icon, onToggleLoginMenu)


    return (
        <div className="relative ">
            <div ref={icon} onClick={onToggleLoginMenu} className='mr-0.5 cursor-pointer'>
                <Icon className='w-6 h-6 sm:w-8 sm:h-8' />
            </div>
            {showControl(
                <div ref={menu} className={'absolute top-10 sm:top-11 sm:left-auto sm:-right-2 ' + stylesX}>
                    <ul className="capitalize w-screen sm:w-72 bg-white text-black text-lg font-bold">
                        {makeNavbar(menuItems)}
                    </ul>
                </div>
            , isMenuOpen)}
        </div>

    )
})

export default Menu
