import React from "react";

type THamburgerContext = {
    isMenuOpen: boolean
    setIsMenuOpen: (isMenuOpen: boolean) => void
}

const HamburegerContext = createContext<THamburgerContext>({
    isMenuOpen,
    setIsMenuOpen
})

export default useHamburgerContext = useContext(HamburegerContext)
