import React, { useRef } from "react";
import { FaUser } from "react-icons/fa";
import makeNavbar from '../../assets/navbarCreater'
import showControl from "../../assets/showControl/showControl";
import { TNavItem } from '../../types/types'

type TProps = {
    menuItems: Array<TNavItem>
    isMenuOpen: boolean 
    setIsMenuOpen: (isMenuOpen: boolean ) => void
    stylesX: string
}

const Menu: React.FC<TProps> = React.memo(({menuItems, setIsMenuOpen, isMenuOpen, stylesX}) => {

    const icon = useRef(null)
    const menu = useRef(null)

    const onToggleLoginMenu = () => {
        setIsMenuOpen(!isMenuOpen)
    }


    return (
        <div className="relative ">
            <div ref={icon} onClick={onToggleLoginMenu} className='mr-0.5 cursor-pointer'>
                <FaUser className='w-6 h-6 sm:w-8 sm:h-8' />
            </div>
            {showControl(
                <div ref={menu} className={'absolute -left-1 top-11 sm:left-auto ' + stylesX}>
                    <ul className="capitalize w-screen sm:w-72 bg-white text-black text-lg font-bold">
                        {makeNavbar(menuItems)}
                    </ul>
                </div>
            , isMenuOpen)}
        </div>

    )
})

export default Menu
