type TProps = {}


const ScrollToTopButton: React.FC<TProps> = React.memo(() => {


    const scrollToTop = () =>  window.scrollTo({
        top: 0,
        behavior: 'smooth'
    })


    return (
        <div onClick={scrollToTop} className='w-16 h-16 bg-slate-400 opacity-50 cursor-pointer'>

        </div>
    )
})

export default ScrollToTopButton
