import React from "react";


type TProps = {
    bannerBg: string
    bannerText: string
}

const Banner: React.FC<TProps> = ({bannerBg, bannerText = ''}) => {

    return (
        <div
        style={{backgroundImage: `url(${bannerBg})`}}
         className='aspect-[4/3] sm:aspect-video lg:aspect-[2.35/1]  bg-center bg-cover bg-no-repeat flex justify-center items-center white'>
             <div>
                {bannerText}
             </div>
        </div>
    )
}


export default Banner
