import React, { LegacyRef, useRef, useState, RefObject } from 'react'
import Hamburger from '../../assets/Hamburger/Hamburger'
import showControl from '../../assets/showControl/showControl'
import NavbarContainer from '../Navbar/NavbarContainer'
import { useOnClickOutside } from "usehooks-ts";




type TProps = {}


const HamburgerMenuContainer: React.FC<TProps> = React.memo(() => {

    const [isMenuOpen, setIsMenuOpen] = useState(false) 

    const onToggleMenu = (event: React.MouseEvent<HTMLDivElement> | MouseEvent): void => {
        setIsMenuOpen(!isMenuOpen)
    }

    const closeMenu = () => {
        setIsMenuOpen(false)
    }

    useOnClickOutside(wrapper, closeMenu)

    return (
        <div ref={wrapper} className='relative'>
            <div ref={icon} onClick={onToggleMenu} className='h-full mt-8 sm:mt-5 ml-0.5 mr-16'>
                <Hamburger menu={menu.current} callback={setIsMenuOpen}/>
            </div>
                    {showControl((
                            <div ref={menu} className='absolute -left-[8px] sm:-left-3'>
                                <NavbarContainer />
                            </div>
                        ), isMenuOpen)}
        </div>     
    )
})

export default HamburgerMenuContainer
