import React, {useState, useRef, LegacyRef, useEffect, RefObject} from 'react'
import { useOnClickOutside } from 'usehooks-ts'
import Header from './Header'


type TProps = {
}

const HeaderContainer: React.FC<TProps> = React.memo((props) => {

    const [isNavbarOpen, setIsNavbarOpen] = useState<boolean>(false)
    const [isLoginMenuOpen, setIsLoginMenuOpen] = useState<boolean>(false)
    const [isCartMenuOpen, setIsCartMenuOpen] = useState<boolean> (false)

    const hamburger = useRef(null)
    const shoppingCart = useRef(null)
    const userIcon = useRef(null)

    const logoText = 'prompostach'






    return <Header 
        isCartMenuOpen={isCartMenuOpen}
        setIsCartMenuOpen={setIsCartMenuOpen}
        isLoginMenuOpen={isLoginMenuOpen} 
        setIsLoginMenuOpen={setIsLoginMenuOpen} 
        isNavbarOpen={isNavbarOpen} 
        logoText={logoText} 
        setIsNavbarOpen={setIsNavbarOpen} 
        hamburger={hamburger}
        shoppingCart={shoppingCart}
        userIcon={userIcon}
        {...props}
        />
})

export default HeaderContainer
