import classNames from "classnames";
import React, { useState, useRef, useEffect } from "react";
import useAutoClose from "../../hoc/useAutoClose";
import styles from './Hamburger.module.css'

type TProps = {
    callback: (isActive: boolean) => void 
}

const Hamburger: React.FC<TProps> = React.memo(({callback}) => {


    const Checkbox = useRef<null | HTMLInputElement>(null);

    useEffect(() => {
        runCallback()
    })



    const onClick = (): void => {
        runCallback()
    }

    const runCallback = (): void => {
        callback(Checkbox.current?.checked === true)
    }


    useEffect(() => {
        const checkIfClickedOutside = (e: Event) => {
          // If the menu is open and the clicked target is not within the menu,
          // then close the menu
          if (Checkbox.current?.checked && Checkbox.current) {
            callback(false)
          }
        }

        document.addEventListener("mousedown", checkIfClickedOutside)

        return () => {
          // Cleanup the event listener
          document.removeEventListener("mousedown", checkIfClickedOutside)
        }
      }, [Checkbox.current?.checked])

    console.log(Checkbox.current?.checked)


    return (
        <div onClick={onClick}>
            <input type="checkbox"  ref={Checkbox} className={styles.menu_checkbox} id='menu_checkbox' />
            <label htmlFor="menu_checkbox">
                <div></div>
                <div></div>
                <div></div>
            </label>
        </div>
)
})

export default Hamburger
