import classNames from "classnames";
import React, {useState} from "react";
import { NavLink} from "react-router-dom";
import Arrow from "../../assets/Arrow/Arrow";
import Container from "../Container/Container";
import { TNavItem } from "./NavbarContainer";


type TProps = {
    navbar: Array<TNavItem>
    catalog: Array<TNavItem>
    languages: Array<TNavItem>
}

const Navbar: React.FC<TProps> = React.memo( ({navbar, catalog, languages}) => {

    const [isCatalogShowing, setIsCatalogShowing] = useState(true)

    const makeMainNavbar = (navItems: Array<TNavItem>) => navItems.map((t) => (

            <li key={t.id}>
                    <NavLink to={t.path}>
                        {t.innerText}
                    </NavLink>
            </li>
    ))

    const arrowClassNames = classNames({
        'absolute': true,
        'top-1': isCatalogShowing,
        '-top-0.3': !isCatalogShowing
    })

    return (
        <Container>
            <ul className="capitalize w-screen text-lg font-bold space-y-2">
                <li className='space-y-2'>
                        <div className="relative">
                            <span className="mr-3">каталог продукции</span>
                            <span className={arrowClassNames}><Arrow isUp={isCatalogShowing} /></span>
                        </div>

                        <ul className="bg-[#222] text-white -ml-1.5 pl-1.5 space-y-2"> 
                                {makeMainNavbar(catalog)}
                        </ul>
                </li>
                    {makeMainNavbar(navbar)}
                    {makeMainNavbar(languages)}
            </ul>
        </Container>

    )
})

export default Navbar
