    const HamburegerContext = createContext<THamburgerContext>({
        isMenuOpen,
        setIsMenuOpen
    })



    return <HamburgerMenu isMenuOpen={isMenuOpen} setIsMenuOpen={setIsMenuOpen} />
})

export default HamburgerMenuContainer
